mapper.insert:
	INSERT INTO orders ( 

	) VALUES (
		#{orderId}, #{memberIdx}, #{price}, #{paymentMethod}, #{orderAddr},
		#{orderNotes}, NOW(), null, '정상'
	)


        SELECT LAST_INSERT_ID()

INSERT INTO orderDetail (
	order_id, product_id, quantity
) 
SELECT #{orderId}, product_id, quantity
FROM cart
WHERE member_id = #{memberId}

mapper.delete:
DELETE FROM cart
WHERE member_id = #{memberIdx}
	AND EXISTS (
		SELECT 1
		FROM member
		WHERE idx = #{memberIdx}
)
